var title_f30_41_31387="Risk classific exercise class D";
var content_f30_41_31387=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F52847&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F52847&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk classification for exercise training: class D: unstable disease with activity restriction*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        This classification includes individuals with any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Unstable ischemia.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Severe and symptomatic valvular stenosis or regurgitation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Congenital heart disease; criteria for risk that would prohibit exercise conditioning in patients with congenital heart disease should be guided by the 27th Bethesda Conference recommendations.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Heart failure that is not compensated.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Uncontrolled arrhythmias.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6. Other medical conditions that could be aggravated by exercise.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Activity guidelines:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. No activity is recommended for conditioning purposes.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Attention should be directed to treating the patient and restoring the patient to Class C or better.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Daily activities must be prescribed on the basis of individual assessment by the patient's personal physician.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Exercise for conditioning purposes is not recommended.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Fletcher GF, Balady GJ, Amsterdam EA, et al. Circulation 2001; 104:1694. Copyright &copy; 2001 Lippincott Williams and Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
