var title_f30_44_31437="Definition metabolic syndrome";
var content_f30_44_31437=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F53446&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F53446&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Five definitions of the metabolic syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Parameters",
"       </td>",
"       <td class=\"subtitle1\">",
"        NCEP ATP3 2005*",
"       </td>",
"       <td class=\"subtitle1\">",
"        IDF 2006",
"       </td>",
"       <td class=\"subtitle1\">",
"        EGIR 1999",
"       </td>",
"       <td class=\"subtitle1\">",
"        WHO 1999",
"       </td>",
"       <td class=\"subtitle1\">",
"        AACE 2003",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Required",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Waist &ge;94 cm (men) or &ge;80 cm (women)",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Insulin resistance or fasting hyperinsulinemia in top 25 percent",
"       </td>",
"       <td>",
"        Insulin resistance in top 25 percent",
"        <sup>",
"         &Delta;",
"        </sup>",
"        ; glucose &ge;6.1 mmol/L (110 mg/dL); 2-hour glucose &ge;7.8 mmol/L (140 mg/dL)",
"       </td>",
"       <td>",
"        High risk of insulin resistance",
"        <sup>",
"         &loz;",
"        </sup>",
"        or BMI &ge;25 kg/m",
"        <sup>",
"         2",
"        </sup>",
"        or waist &ge;102 cm (men) or &ge;88 cm (women)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Number of abnormalities",
"       </td>",
"       <td>",
"        &ge;3 of:",
"       </td>",
"       <td>",
"        And &ge;2 of:",
"       </td>",
"       <td>",
"        And &ge;2 of:",
"       </td>",
"       <td>",
"        And &ge;2 of:",
"       </td>",
"       <td>",
"        And &ge;2 of:",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Glucose",
"       </td>",
"       <td>",
"        &ge;5.6 mmol/L (100 mg/dL) or drug treatment for elevated blood glucose",
"       </td>",
"       <td>",
"        &ge;5.6 mmol/L (100 mg/dL) or diagnosed diabetes",
"       </td>",
"       <td>",
"        6.1-6.9 mmol/ (110-125 mg/dL)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &ge;6.1 mmol/L (110 mg/dL); &ge;2-hour glucose 7.8 mmol/L (140 mg/dL)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        HDL cholesterol",
"       </td>",
"       <td>",
"        &lt;1.0 mmol/L (40 mg/dL) (men); &lt;1.3 mmol/L (50 mg/dL) (women) or drug treatment for low HDL-C",
"        <sup>",
"         &sect;",
"        </sup>",
"       </td>",
"       <td>",
"        &lt;1.0 mmol/L (40 mg/dL) (men); &lt;1.3 mmol/L (50 mg/dL) (women) or drug treatment for low HDL-C",
"       </td>",
"       <td>",
"        &lt;1.0 mmol/L (40 mg/dL)",
"       </td>",
"       <td>",
"        &lt;0.9 mmol/L (35 mg/dL) (men); &lt;1.0 mmol/L (40 mg/dL) (women)",
"       </td>",
"       <td>",
"        &lt;1.0 mmol/L (40 mg/dL) (men); &lt;1.3 mmol/L (50 mg/dL) (women)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Triglycerides",
"       </td>",
"       <td>",
"        &ge;1.7 mmol/L (150 mg/dL) or drug treatment for elevated triglycerides",
"        <sup>",
"         &sect;",
"        </sup>",
"       </td>",
"       <td>",
"        &ge;1.7 mmol/L (150 mg/dL) or drug treatment for high triglycerides",
"       </td>",
"       <td>",
"        or &ge;2.0 mmol/L (180 mg/dL) or drug treatment for dyslipidemia",
"       </td>",
"       <td>",
"        or &ge;1.7 mmol/L (150 mg/dL)",
"       </td>",
"       <td>",
"        &ge;1.7 mmol/L (150 mg/dL)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Obesity",
"       </td>",
"       <td>",
"        Waist &ge;102 cm (men) or &ge;88 cm (women)",
"        <sup>",
"         &yen;",
"        </sup>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Waist &ge;94 cm (men) or &ge;80 cm (women)",
"       </td>",
"       <td>",
"        Waist/hip ratio &gt;0.9 (men) or &gt;0.85 (women) or BMI &ge;30 kg/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypertension",
"       </td>",
"       <td>",
"        &ge;130/85 mmHg or drug treatment for hypertension",
"       </td>",
"       <td>",
"        &ge;130/85 mmHg or drug treatment for hypertension",
"       </td>",
"       <td>",
"        &ge;140/90 mmHg or drug treatment for hypertension",
"       </td>",
"       <td>",
"        &ge;140/90 mmHg",
"       </td>",
"       <td>",
"        &ge;130/85 mmHg",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     NCEP: National Cholesterol Education Program; IDF: International Diabetes Federation; EGIR: Group for the Study of Insulin Resistance; WHO: World Health Organization; AACE: American Association of Clinical Endocrinologists; HDL: high density lipoprotein; BMI: body mass index.",
"     <br/>",
"     * Most commonly agreed upon criteria for metabolic syndrome (any three of five risk factors).",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     For South Asia and Chinese patients, waist &ge;90 cm (men) or &ge;80 cm (women); for Japanese patients, waist &ge;90 cm (men) or &ge;80 cm (women).",
"     <br/>",
"     &Delta; Insulin resistance measured using insulin clamp.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     High risk of being insulin resistant is indicated by the presence of at least one of the following: diagnosis of CVD, hypertension, polycystic ovary syndrome, non-alcoholic fatty liver disease or acanthosis nigricans; family history of type 2 diabetes, hypertension of CVD; history of gestational diabetes or glucose intolerance; nonwhite ethnicity; sedentary lifestyle; BMI 25 kb/m",
"     <sup>",
"      2",
"     </sup>",
"     or waist circumference 94 cm for men and 80 cm for women; and age 40 years.",
"     <br/>",
"     &sect; Treatment with one or more of fibrates or niacin.",
"     <br/>",
"     &yen; In Asian patients, waist &ge;90 cm (men) or &ge;80 cm (women).",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Alberti KG, Eckel RH, Grundy SM, et al. Harmonizing the metabolic syndrome: a joint interim statement of the International Diabetes Federation Task Force on Epidemiology and Prevention; National Heart, Lung, and Blood Institute; American Heart Association; World Heart Federation; International Atherosclerosis Society; and International Association for the Study of Obesity. Circulation 2009; 120:1640.",
"      </li>",
"      <li>",
"       Meigs James. Metabolic syndrome and the risk for type 2 diabetes. Expert Rev Endocrin Metab 2006; 1:57. Table 1. Updated data from the International Diabetes Federation, 2006. Available at:",
"       <a href=\"file://www.idf.org/webdata/docs/MetS_def_update2006.pdf\" target=\"_blank\">",
"        file://www.idf.org/webdata/docs/MetS_def_update2006.pdf",
"       </a>",
"       .",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
