"  <div id=\"topicTitle\">",
"   Patient information: Hepatitis B (Beyond the Basics)",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/45/31447/contributors\">",
"     Author",
"    </a>",
"    <br/>",
