" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of Candida infections in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/47/31481/contributors\">",
