var title_f30_47_31484="Increased risk oral infection";
var content_f30_47_31484=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56703&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56703&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Conditions that are associated with increased risk of infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Condition",
"      </td>",
"      <td class=\"subtitle1\">",
"       Oral manifestation or complication",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dental management",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Childhood cancers",
"      </td>",
"      <td>",
"       Decreased immune function, leading to gingival inflammation and alveolar bone loss",
"      </td>",
"      <td>",
"       Reduction of plaque and microorganisms through oral hygiene and use of chemotherapeutic agents, such as chlorhexidine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chemotherapeutic immunosuppression",
"      </td>",
"      <td>",
"       Oral ulceration; infection by opportunistic organisms",
"      </td>",
"      <td>",
"       Reduction of plaque and microorganisms through oral hygiene and use of chemotherapeutic agents, such as chlorhexidine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Graft-versus-host disease",
"      </td>",
"      <td>",
"       Mucositis, xerostomia, oral pain",
"      </td>",
"      <td>",
"       Artificial saliva, chemotherapeutic agents to reduce plaque, pain control measures",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       HIV infection",
"      </td>",
"      <td>",
"       Severe acute and chronic gingival and periodontal inflammation, exceeding that expected for local irritants present",
"      </td>",
"      <td>",
"       Reduction of plaque and microorganisms through oral hygiene and use of chemotherapeutic agents, such as chlorhexidine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diabetes mellitus",
"      </td>",
"      <td>",
"       Increased gingival and periodontal inflammation and increased risk of odontogenic infections",
"      </td>",
"      <td>",
"       Reduction of plaque and microorganisms through oral hygiene and use of chemotherapeutic agents, such as chlorhoxidine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Congenital heart disease",
"      </td>",
"      <td>",
"       Cyanotic friable oral tissues; ingress of microorganisms implicated in subacute bacterial endocarditis",
"      </td>",
"      <td>",
"       Adherence to American Heart Association guidelines for prevention of infective endocarditis [1]",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       In-dwelling catheters/central lines",
"      </td>",
"      <td>",
"       Risk of infection of device from oral bacteremia introduced by dental treatment",
"      </td>",
"      <td>",
"       Adherence to American Heart Association guidelines for prevention of infective endocarditis [1]",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sickle cell anemia",
"      </td>",
"      <td>",
"       Nonspecific oral findings, but at increased risk for infection secondary to dental treatment in some cases",
"      </td>",
"      <td>",
"       Prophylaxis with appropriate antibiotic when needed for dental treatment",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     1. Wilson, W, Taubert, KA, Gewitz, M, et al. Prevention of infective endocarditis: guidelines from the American Heart Association: a guideline from the American Heart Association Rheumatic Fever, Endocarditis, and Kawasaki Disease Committee, Council on Cardiovascular Disease in the Young, and the Council on Clinical Cardiology, Council on Cardiovascular Surgery and Anesthesia, and the Quality of Care and Outcomes Research Interdisciplinary Working Group. Circulation 2007; 116:1736.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from Casamassimo, PS. Pediatr Clin North Am 2000; 47:1149. Copyright 2000 W.B. Saunders Co.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
