" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cyclospora infection",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/48/31493/contributors\">",
