" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Radiofrequency ablation for the treatment of lower extremity chronic venous disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/5/30809/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?30/5/30809/contributors\">",
"     Sherry Scovell, MD, FACS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/5/30809/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?30/5/30809/contributors\">",
"     John F Eidt, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?30/5/30809/contributors\">",
"     Joseph L Mills, Sr, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/5/30809/contributors\">",
"     Deputy Editor",
"    </a>",
