" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations and natural history of chronic hepatitis C virus infection",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/50/31530/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?30/50/31530/contributors\">",
"     Sanjiv Chopra, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/50/31530/contributors\">",
"     Section Editor",
