"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.AAGradient_form){",
"",
"if (Age_param.value && Age < 0) {",
"Age = 0;",
"Age_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Age is 0 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Age_param.value && Age > 120) {",
"Age_param.value = \"\";",
"clrResults();",
"Age = 0;",
"doCalc = false;",
"alert(\"The maximum value for Age is 120 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Patient_Temp_param.value && Patient_Temp < 20) {",
"Patient_Temp = 0;",
"Patient_Temp_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Patient Temp is 20 degC.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Patient_Temp_param.value && Patient_Temp > 40) {",
"Patient_Temp_param.value = \"\";",
"clrResults();",
"Patient_Temp = 0;",
"doCalc = false;",
"alert(\"The maximum value for Patient Temp is 40 degC.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Elevation_param.value && Elevation < 0) {",
"Elevation = 0;",
"Elevation_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Elevation is 0 m.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Elevation_param.value && Elevation > 10000) {",
"Elevation_param.value = \"\";",
"clrResults();",
"Elevation = 0;",
"doCalc = false;",
"alert(\"The maximum value for Elevation is 10000 m.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Percent_Inspired_O2_param.value && Percent_Inspired_O2 < 1) {",
"Percent_Inspired_O2 = 0;",
"Percent_Inspired_O2_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Percent Inspired O2 is 1 %O2.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Percent_Inspired_O2_param.value && Percent_Inspired_O2 > 100) {",
"Percent_Inspired_O2_param.value = \"\";",
"clrResults();",
"Percent_Inspired_O2 = 0;",
"doCalc = false;",
"alert(\"The maximum value for Percent Inspired O2 is 100 %O2.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Resp_Quot_param.value && Resp_Quot < 0.01) {",
"Resp_Quot = 0;",
"Resp_Quot_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Resp Quot is 0.01 ratio.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Resp_Quot_param.value && Resp_Quot > 100) {",
"Resp_Quot_param.value = \"\";",
"clrResults();",
"Resp_Quot = 0;",
"doCalc = false;",
"alert(\"The maximum value for Resp Quot is 100 ratio.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
