var title_f30_51_31548="ACC AHA angiography for stable angina";
var content_f30_51_31548=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F63658&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F63658&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Coronary angiography for risk stratification in patients with chronic stable angina",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general",
"agreement that coronary angiography should be performed to risk",
"stratify patients with chronic stable angina in the following settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Disabling anginal symptoms (Canadian Cardiovascular Society [CCS] classes III and IV) despite medical therapy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; High-risk criteria on noninvasive testing independent of the severity of angina.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Survivors of sudden cardiac death or serious ventricular arrhythmia.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Symptoms and signs of heart failure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Clinical features that suggest that the patient has a high likelihood of severe coronary artery disease.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa - The evidence or",
"opinion is in favor of performing coronary angiography to risk stratify",
"patients with chronic stable angina in the following settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Left ventricular ejection fraction less than 45 percent, CCS class I or II angina, and evidence, on noninvasive testing, of ischemia that does not meet high-risk criteria.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Noninvasive testing does not reveal adequate prognostic information.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIb - The evidence or",
"opinion is less well established for performing coronary angiography to",
"risk stratify patients with chronic stable angina in the following",
"settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Left ventricular ejection fraction greater than 45 percent, CCS class I or II angina, and evidence, on noninvasive testing, of ischemia that does not meet high-risk criteria.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; CCS class III or IV angina that improves to class I or II with medical therapy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; CCS class I or II angina but unacceptable side effects to adequate medical therapy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III - There is evidence",
"and/or general agreement that coronary angiography should not be",
"performed to risk stratify patients with chronic stable angina in the",
"following settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; CCS class I or II angina that responds to medical therapy and, on noninvasive testing, shows no evidence of ischemia.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Patient preference to avoid revascularization.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Gibbons RJ, Abrams J, Chatterjee K, et al. ACC/AHA 2002 guideline update for the management of patients with chronic stable angina--summary article: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Committee on the Management of Patients With Chronic Stable Angina). Circulation 2003; 107:149.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
