" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Human herpesvirus 6 infection in children: Clinical manifestations; diagnosis; and treatment",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/53/31577/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/53/31577/contributors\">",
"     Cecile Tremblay, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/53/31577/contributors\">",
"     Michael T Brady, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/53/31577/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/53/31577/contributors\">",
"     Sheldon L Kaplan, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/53/31577/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/53/31577/contributors\">",
"     Mary M Torchia, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
