" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Initial and reoperative thyroidectomy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/57/31642/contributors\">",
