" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Exenteration for gynecologic cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/60/31689/contributors\">",
