var title_f30_60_31691="Monitoring MEN2 FMTC";
var content_f30_60_31691=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F77855&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F77855&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Periodic clinical monitoring of carriers of an MEN2 or FMTC mutation depends on specific mutation in the RET gene",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Medullary thyroid carcinoma (MTC)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Preceding and after total thyroidectomy: annual utrasound of the neck",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Annual basal serum calcitonin and CEA level; if elevated at least biannual",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Pheochromocytoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Codons 634, 918",
"       </td>",
"       <td>",
"        From the age of&nbsp;five annually at least: serum fractionated metanephrines and/or normetanephrine / metanephrine in 24-hour urine collection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Codons 533, 609, 611, 618, 620, 630, 633, 666, 768, 790, 791, 804, 891",
"       </td>",
"       <td>",
"        From the age of 10 biennial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Hyperparathyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Codon 634",
"       </td>",
"       <td>",
"        Annual calcium, PTH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Codons 609, 611, 618, 620, 790, 791",
"       </td>",
"       <td>",
"        From the age of 10 biennial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Codons 768, 804, 891",
"       </td>",
"       <td>",
"        Hyperparathyroidism occurs infrequently",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Codons 883, 918, 922 (MEN2B)",
"       </td>",
"       <td>",
"        Hyperparathyroidism is not a feature",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
