var title_f30_8_30859="Risk factors for hearing loss";
var content_f30_8_30859=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F74313&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F74313&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk factors for sensorineural hearing loss in the newborn",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Genetic",
"      </td>",
"      <td class=\"subtitle1\">",
"       Prenatal",
"      </td>",
"      <td class=\"subtitle1\">",
"       Postnatal",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Autosomal recessive",
"      </td>",
"      <td>",
"       Perinatal infections (CMV, Syphilis, Toxoplasmosis, Rubella, HIV)",
"      </td>",
"      <td>",
"       Very low birth weight (&lt;1500g)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Autosomal dominant",
"      </td>",
"      <td>",
"       Maternal diabetes",
"      </td>",
"      <td>",
"       Ischemia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       X-linked",
"      </td>",
"      <td>",
"       Prenatal medication (antimalarials, aminoglycosides isoretinoin)",
"      </td>",
"      <td>",
"       Severe Hyperbilirubinemia (requiring exchange transfusion)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       Mitochondrial",
"      </td>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       <p>",
"        Maternal drug abuse",
"       </p>",
"       <p>",
"        Lack of prenatal care",
"       </p>",
"      </td>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       <p>",
"        Low APGAR score (&lt;4 at one min or &lt;6 at five min)",
"       </p>",
"       <p>",
"        Mechanical ventilation &gt; five days",
"       </p>",
"       <p>",
"        Meningitis (Bacterial , fungal)",
"       </p>",
"       <p>",
"        Ototoxic medications (aminoglycosides*, diuretics)",
"       </p>",
"       <p>",
"        ECMO",
"       </p>",
"       <p>",
"        PPHN",
"       </p>",
"       <p>",
"        Hypothyroidism",
"       </p>",
"       <p>",
"        ? Noise exposure",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    *Certain genetic predispositions appear to increase an infant's risk of aminoglycoside-related hearing loss",
"    <br>",
"     CMV: Cytomegalovirus infection; PPHN:Persistent pulmonary hypertension of the newborn; ECMO:Extracorporeal membrane oxygenation",
"     <div class=\"footnotes\">",
"     </div>",
"     <div class=\"reference\">",
"     </div>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
