var title_f30_8_30860="Physiologic Rx Malnutrition B";
var content_f30_8_30860=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F54640&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F54640&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Physiologic basis for treatment of severe malnutrition",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Affected organ or system",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effects",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immune system",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         All aspects of immunity are diminished",
"        </p>",
"        <p>",
"         Lymph glands, tonsils and the thymus are atrophied",
"        </p>",
"        <p>",
"         Cell-mediated (T-cell) immunity is severely depressed",
"        </p>",
"        <p>",
"         IgA levels in secretions are reduced",
"        </p>",
"        <p>",
"         Complement components are low",
"        </p>",
"        <p>",
"         Phagocytes do not kill ingested bacteria efficiently",
"        </p>",
"        <p>",
"         Tissue damage does not result in inflammation or migration of white cells to the affected area",
"        </p>",
"        <p>",
"         Acute phase immune response is diminished",
"        </p>",
"        <p>",
"         Typical signs of infection, such as an increased white cell count and fever, are frequently absent",
"        </p>",
"        <p>",
"         Hypoglycaemia and hypothermia are both signs of severe infection and are usually associated with septic shock",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Treat all children with broad-spectrum antimicrobials",
"        </p>",
"        <p>",
"         Because of the risk of transmission of infection, ensure that newly",
"admitted children are kept apart from children who are recovering from",
"infection",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Endocrine system",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Insulin levels are reduced and the child has impaired glucose tolerance",
"        </p>",
"        <p>",
"         Insulin growth factor 1 (IGF-1) levels are reduced, although growth hormone levels are increased",
"        </p>",
"        <p>",
"         Cortisol levels are usually increased",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Give the child small, frequent feeds",
"        </p>",
"        <p>",
"         Do not give steroids",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Circulatory system",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Basic metabolic rate is reduced by about 30 percent",
"        </p>",
"        <p>",
"         Energy expenditure due to activity is very low",
"        </p>",
"       </td>",
"       <td>",
"        Keep the child warm to prevent",
"hypothermia; dry the child quickly and properly after washing and cover",
"with clothes and blankets, ensure that windows are kept closed at night",
"and keep the temperature of the living environment at 25-30 &ordm;C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Both",
"heat generation and heat loss are impaired; the child becomes",
"hypothermic in a cold environment and hyperthermic in a hot environment",
"       </td>",
"       <td>",
"        If a child has fever, cool the child by sponging with tepid (not cold) water (never alcohol rubs)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Management of Severe Malnutrition: A Manual for Physicians and other Senior Health Workers. World Health Organization, Geneva, 1999. Copyright &copy; 1999 World Health Organization.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
