var title_f31_0_31757="Preferred and alternative regimens for ART-naive patients";
var content_f31_0_31757=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F70143&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F70143&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Preferred and alternative regimens for antiretroviral therapy-na&iuml;ve patients",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td colspan=\"2\">",
"        Selection of a regimen should be individualized based on virologic efficacy, toxicity, pill burden, dosing frequency, drug-drug interaction potential, resistance testing results, and comorbid conditions. The regimens in each category are listed in alphabetical order.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Preferred regimens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        (Regimens with optimal and durable efficacy, favorable tolerability and toxicity profile, and ease of use.)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        The preferred regimens for nonpregnant patients are arranged by order of FDA approval of components other than nucleosides, thus, by duration of clinical experience.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         NNRTI-based regimen",
"        </strong>",
"        <ul>",
"         <li>",
"          EFV/TDF/FTC&Delta; (AI)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         PI-based regimens",
"        </strong>",
"        <em>",
"         (in alphabetical order)",
"        </em>",
"        <ul>",
"         <li>",
"          ATV/r + TDF/FTC&Delta; (AI)",
"         </li>",
"         <li>",
"          DRV/r + TDF/FTC&Delta; (AI)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         INSTI-based regimen",
"        </strong>",
"        <ul>",
"         <li>",
"          RAL + TDF/FTC&Delta; (AI)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         Preferred regimen&loz; for pregnant women",
"        </strong>",
"        <ul>",
"         <li>",
"          LPV/r + ZDV/3TC&Delta; (AI)",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <strong>",
"         Comments",
"        </strong>",
"        <hr/>",
"        <p>",
"         <strong>",
"          EFV",
"         </strong>",
"         should not be used during the first trimester of pregnancy or in women trying to conceive or not using effective and consistent contraception.",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         <strong>",
"          TDF",
"         </strong>",
"         should be used with caution in patients with renal insufficiency.",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         <strong>",
"          ATV/r",
"         </strong>",
"         should not be used in patients who require &gt;20 mg omeprazole equivalent per day.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Alternative regimens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        (Regimens that are effective and tolerable but have potential disadvantages compared with preferred regimens. An alternative regimen may be the preferred regimen for some patients.)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         NNRTI-based regimens",
"        </strong>",
"        <em>",
"         (in alphabetical order)",
"        </em>",
"        <ul>",
"         <li>",
"          EFV + (ABC or ZDV)/3TC&Delta; (BI)",
"         </li>",
"         <li>",
"          RPV/TDF/FTC&Delta; (BI)",
"         </li>",
"         <li>",
"          RPV + ABC/3TC&Delta; (BIII)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         PI-based regimens",
"        </strong>",
"        <em>",
"         (in alphabetical order)",
"        </em>",
"        <ul>",
"         <li>",
"          ATV/r + (ABC or ZDV)/3TC&Delta; (BI)",
"         </li>",
"         <li>",
"          DRV/r + ABC/3TC&Delta; (BIII)",
"         </li>",
"         <li>",
"          FPV/r + either [(ABC or ZDV)/3TC&Delta;] or TDF/FTC&Delta; (BI)",
"         </li>",
"         <li>",
"          LPV/r + either [(ABC or ZDV)/3TC&Delta;] or TDF/FTC&Delta; (BI)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <p>",
"         <strong>",
"          INSTI-based regimen",
"         </strong>",
"        </p>",
"        <ul>",
"         <li>",
"          RAL + ABC/3TC&Delta; (BIII)&nbsp;",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Comments",
"         </strong>",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <hr/>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         <strong>",
"          RPV",
"         </strong>",
"        </p>",
"        <ul>",
"         <li>",
"          Use RPV with caution in patients with pretreatment HIV RNA &gt;100,000 copies/mL",
"         </li>",
"         <li>",
"          Use of proton pump inhibitors is contraindicated with RPV",
"         </li>",
"        </ul>",
"        <p>",
"         <strong>",
"          ABC",
"         </strong>",
"        </p>",
"        <ul>",
"         <li>",
"          ABC should not be used in patients who test positive for HLA-B*5701",
"         </li>",
"         <li>",
"          Use ABC with caution in patients with high risk of cardiovascular disease or with pretreatment",
"          <br/>",
"          HIV RNA &gt;100,000 copies/mL",
"         </li>",
"        </ul>",
"        <br/>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The following combinations in the recommended list above are available as fixed-dose combination formulations: ABC/3TC, EFV/TDF/FTC, LPV/r, RPV/TDF/FTC, TDF/FTC, and ZDV/3TC.",
"    <br>",
"     <div class=\"footnotes\">",
"      3TC: lamivudine; ABC: abacavir; ATV: atazanavir; ATV/r: atazanavir/ritonavir; DRV: darunavir; DRV/r: darunavir/ritonavir; EFV: efavirenz; FPV: fosamprenavir; FPV/r: fosamprenavir/ritonavir; FTC: emtricitabine; INSTI: integrase strand transfer inhibitor; LPV: lopinavir; LPV/r: lopinavir/ritonavir; NNRTI: non-nucleoside reverse transcriptase inhibitor; NRTI: nucleos(t)ide reverse transcriptase inhibitor; PI: protease inhibitor; RAL: raltegravir; RPV: rilpivirine; RTV: ritonavir; TDF: tenofovir; ZDV: zidovudine.",
"      <br>",
"       Rating of Recommendations: A = Strong; B = Moderate; C = Optional Rating of Evidence: I = data from randomized controlled trials; II = data from well-designed nonrandomized trials or observational cohort studies with long-term clinical outcomes; III = expert opinion.",
"       <br>",
"        &Delta; 3TC may substitute for FTC or vice versa.",
"        <br>",
"         <span class=\"lozenge\">",
"          &loz;",
"         </span>",
"         For more detailed recommendations on ARV use in an HIV-infected pregnant woman, refer to the Perinatal guidelines available at file://aidsinfo.nih.gov/guidelines.",
"        </br>",
"       </br>",
"      </br>",
"     </div>",
"     <div class=\"reference\">",
"      Guidelines for the use of antiretroviral agents in HIV-1-infected adults and adolescents, October 14, 2011. United States Department of Health and Human Services.",
"     </div>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
