var content_f31_11_31933=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F74747&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F74747&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Proposed IASLC ATS ERS classification for small biopsies and cytology",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        2004 WHO Classification",
"       </td>",
"       <td class=\"subtitle1\">",
"        Small biopsy/cytology: IASLC/ATS/ERS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Adenocarcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <p>",
"         Mixed subtype",
"        </p>",
"        <p>",
"         Acinar",
"        </p>",
"        <p>",
"         Papillary",
"        </p>",
"        <p>",
"         Solid",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <em>",
"          Morphologic adenocarcinoma patterns clearly present:",
"         </em>",
"        </p>",
"        <p>",
"         Adenocarcinoma, describe identifiable patterns present (including micropapillary pattern not included in 2004 WHO classification)",
"        </p>",
"        <p>",
"         Comment: If pure lepidic growth - mention an invasive component cannot be excluded in this small specimen.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bronchioloalveolar carcinoma (nonmucinous)",
"       </td>",
"       <td>",
"        Adenocarcinoma with lepidic pattern (if pure, add note: an invasive component cannot be excluded)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bronchioloalveolar carcinoma (mucinous)",
"       </td>",
"       <td>",
"        Mucinous adenocarcinoma (describe patterns present)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fetal",
"       </td>",
"       <td>",
"        Adenocarcinoma with fetal pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mucinous (colloid)",
"       </td>",
"       <td>",
"        Adenocarcinoma with colloid pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Signet ring",
"       </td>",
"       <td>",
"        Adenocarcinoma with (describe patterns present) and signet ring features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clear cell",
"       </td>",
"       <td>",
"        Adenocarcinoma with (describe patterns present) and clear cell features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No 2004 WHO counterpart - most will be solid adenocarcinomas",
"       </td>",
"       <td>",
"        <p>",
"         <em>",
"          Morphologic adenocarcinoma patterns not present (supported by special stains):",
"         </em>",
"        </p>",
"        <p>",
"         Non-small cell carcinoma, favor adenocarcinoma",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Squamous cell carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <p>",
"         Papillary",
"        </p>",
"        <p>",
"         Clear cell",
"        </p>",
"        <p>",
"         Small cell",
"        </p>",
"        <p>",
"         Basaloid",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <em>",
"          Morphologic squamous cell patterns clearly present:",
"         </em>",
"        </p>",
"        <p>",
"         Squamous cell carcinoma",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No 2004 WHO counterpart",
"       </td>",
"       <td>",
"        <p>",
"         <em>",
"          Morphologic squamous cell patterns not present (supported by stains):",
"         </em>",
"        </p>",
"        <p>",
"         Non-small cell carcinoma, favor squamous cell carcinoma",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Small cell carcinoma",
"        </strong>",
"       </td>",
"       <td>",
"        Small cell carcinoma",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Large cell carcinoma",
"        </strong>",
"       </td>",
"       <td>",
"        Non-small cell carcinoma, not otherwise specified (NOS)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Large cell neuroendocrine carcinoma (LCNEC)",
"       </td>",
"       <td>",
"        Non-small cell carcinoma with neuroendocrine (NE) morphology (positive NE markers), possible LCNEC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Large cell carcinoma with NE morphology (LCNEM)",
"       </td>",
"       <td>",
"        <p>",
"         Non-small cell carcinoma with NE morphology (negative NE markers)",
"        </p>",
"        <p>",
"         Comment: This is a non-small cell carcinoma where LCNEC is suspected, but stains failed to demonstrate NE differentiation.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Adenosquamous carcinoma",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         <em>",
"          Morphologic squamous cell and adenocarcinoma patterns present:",
"         </em>",
"        </p>",
"        <p>",
"         Non-small cell carcinoma, with squamous cell and adenocarcinoma patterns",
"        </p>",
"        <p>",
"         Comment: This could represent adenosquamous carcinoma.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No counterpart in 2004 WHO classification",
"       </td>",
"       <td>",
"        <p>",
"         <em>",
"          Morphologic squamous cell or adenocarcinoma patterns not present but immunostains favor separate glandular and adenocarcinoma components",
"         </em>",
"        </p>",
"        <p>",
"         Non-small cell carcinoma, NOS, (specify the results of the immunohistochemical stains and the interpretation)",
"        </p>",
"        <p>",
"         Comment: This could represent adenosquamous carcinoma.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Sarcomatoid carcinoma",
"        </strong>",
"       </td>",
"       <td>",
"        Poorly differentiated NSCLC with spindle and/or giant cell carcinoma (mention if adenocarcinoma or squamous carcinoma are present)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IASLC: International Association for the Study of Lung Cancer; ATS: American Thoracic Society; ERS: European Respiratory Society; WHO: World Health Organization; NSCLC: non-small cell lung cancer; IHC: immunohistochemistry; TTF: thyroid transcription factor.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Travis WD, Brambilla E, Noguchi M, et al. International Association for the Study of Lung Cancer/American Thoracic Society/European Respiratory Society International Multidisciplinary Classification of Lung Adenocarcinoma. J Thorac Oncol 2011; 6:244. Copyright &copy; 2011 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
