var title_f31_13_31964="Pressure Ulcer Scale for Healing";
var content_f31_13_31964=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F63890&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F63890&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pressure Ulcer Scale for Healing (PUSH)*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Assessments",
"       </td>",
"       <td class=\"subtitle1\">",
"        Instructions",
"       </td>",
"       <td class=\"subtitle1\">",
"        Assign a subscore (cm",
"        <sup>",
"         2",
"        </sup>",
"        )",
"       </td>",
"       <td class=\"subtitle1\">",
"        Total the subscores",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"11\">",
"        Size (length x width)",
"       </td>",
"       <td rowspan=\"11\">",
"        Measure the greatest length and width using a centimeter ruler. Multiply the two measurements to obtain an estimate of surface area.",
"       </td>",
"       <td>",
"        0 - 0",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Subscore",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 - &lt;0.3",
"       </td>",
"       <td rowspan=\"10\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 - 0.3-0.6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 - 0.7-1.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 - 1.1-2.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5 - 2.1-3.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6 - 3.1-4.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        7 - 4.1-8.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        8 - 8.1-12",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        9 - 12.1-24.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        10 - &gt;24",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Exudate",
"       </td>",
"       <td rowspan=\"4\">",
"        Estimate the amount of drainage after removal of the dressing.",
"       </td>",
"       <td>",
"        0 - None",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Subscore",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 - Light",
"       </td>",
"       <td rowspan=\"3\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 - Moderate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 - Heavy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        Tissue type",
"       </td>",
"       <td rowspan=\"5\">",
"        Assess the presence of sloughing or necrosis",
"       </td>",
"       <td>",
"        0 - Closed",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Subscore",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 - Epithelial tissue",
"       </td>",
"       <td rowspan=\"4\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 - Granulation tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 - Slough",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 - Necrotic tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Add together all subscores =",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Total score",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Changes in the score over time provide an indication of the healing process. The score goes down with improvement and increases with wound deterioration.",
"    <div class=\"footnotes\">",
"     * Version 3.0 National Pressure Ulcer Advisory Panel (www.npuap.org).",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: National Pressure Ulcer Advisory Panel. Version 3.0 Pressure Ulcer Scale for Healing (PUSH). Copyright &copy;1998 National Pressure Ulcer Advisory Panel.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
