var title_f31_16_32011="Approach high K RAAS blockers";
var content_f31_16_32011=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F67783&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F67783&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Approach to patients at risk for hyperkalemia caused by inhibitors of the renin-angiotensin-aldosterone system",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Estimate glomerular filtration rate to assess specific risk of hyperkalemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discontinue nonsteroidal antiinflammatory drugs and other drugs that interfere with renal potassium excretion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inquire about use of herbal preparations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prescribe a low-potassium diet",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inquire about use of potassium-containing salt substitutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prescribe thiazide or loop diuretics to increase potassium excretion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prescribe sodium bicarbonate to correct metabolic acidosis if present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        When initiating ACE inhibitor, angiotensin receptor blocker, or aldosterone receptor blocker therapy, use low doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Measure plasma potassium concentration one week after initiating therapy or after increasing dose of these agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If plasma potassium increases to &le;5.5 mmol/liter, decrease dose of drug and, if combination therapy is being given, discontinue one agent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In plasma potassium increases to &gt;5.5 mmol/liter despite above measures, discontinue these agents",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Palmer BF. Managing hyperkalemia caused by inhibitors of the renin- angiotensin-aldosterone system. N Engl J Med 2004; 351:585.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
