" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Endobronchial brachytherapy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/18/32038/contributors\">",
"     Author",
"    </a>",
