var title_f31_18_32043="Neuro complications of hypothy";
var content_f31_18_32043=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F72975&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F72975&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of the neurologic manifestations of hypothyroidism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cardinal features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Relative frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myxedema coma",
"       </td>",
"       <td>",
"        Decreased mental status, bradycardia, hypothermia, hypotension, others",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cognitive impairment",
"       </td>",
"       <td>",
"        Slowed mentation, poor concentration and short-term memory",
"       </td>",
"       <td>",
"        Common in overt hypothyroidism; uncertain in subclinical hypothyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cerebellar ataxia",
"       </td>",
"       <td>",
"        Gait most often affected, limb and speech involvement can occur",
"       </td>",
"       <td>",
"        Uncertain; early studies suggest 10 to 30 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carpal tunnel syndrome",
"       </td>",
"       <td>",
"        Pain, numbness, weakness in median nerve distribution",
"       </td>",
"       <td>",
"        Common 25 to 33 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral neuropathy",
"       </td>",
"       <td>",
"        Predominantly sensory, often painful",
"       </td>",
"       <td>",
"        Common, but rarely the presenting symptom",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myopathy",
"       </td>",
"       <td>",
"        Ranges from asymptomatic serum CK elevation to cramps and myalgias to more disabling proximal muscle weakness",
"       </td>",
"       <td>",
"        Common in at least one form",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
