var title_f31_2_31788="Sporo treatment recs";
var content_f31_2_31788=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F50353&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F50353&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of sporotrichosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Manifestation",
"      </td>",
"      <td class=\"subtitle1\">",
"       Preferred treatment",
"      </td>",
"      <td class=\"subtitle1\">",
"       Alternative treatment",
"      </td>",
"      <td class=\"subtitle1\">",
"       Comments",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lymphocutaneous/cutaneous",
"      </td>",
"      <td>",
"       Itr 200 mg/day",
"      </td>",
"      <td>",
"       Itr 200 mg bid; or terbinafine 500 mg bid; or SSKI with increasing doses; or fluconazole 400-800 mg/day; or local hyperthermia",
"      </td>",
"      <td>",
"       Treat for 2-4 weeks after lesions resolved",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Osteoarticular",
"      </td>",
"      <td>",
"       Itr 200 mg bid",
"      </td>",
"      <td>",
"       Lipid AmB 3-5 mg/kg/day; or deoxycholate AmB 0.7-1 mg/kg/day",
"      </td>",
"      <td>",
"       Switch to Itr after favorable response if AmB used; treat for a total of at least 12 months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pulmonary",
"      </td>",
"      <td>",
"       Lipid AmB 3-5 mg/kg/day, then Itr 200 mg bid; or Itr 200 mg bid",
"      </td>",
"      <td>",
"       Deoxycholate AmB 0.7-1 mg/kg/d, then Itr 200 mg bid; surgical removal",
"      </td>",
"      <td>",
"       Treat severe disease with an AmB formulation followed by Itr; treat less severe disease with Itr; treat for a total of at least 12 months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Meningitis",
"      </td>",
"      <td>",
"       Lipid AmB 5 mg/kg/day, then Itr 200 mg bid",
"      </td>",
"      <td>",
"       Deoxycholate AmB 0.7-1 mg/kg/day, then Itr 200 mg bid",
"      </td>",
"      <td>",
"       Length of therapy with AmB not established, but therapy for at least 4-6 weeks is recommended; treat for a total of at least 12 months; may require long-term suppression with Itr",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Disseminated",
"      </td>",
"      <td>",
"       Lipid AmB 3-5 mg/kg/day, then Itr 200 mg bid",
"      </td>",
"      <td>",
"       Deoxycholate AmB 0.7-1 mg/kg/day, then Itr 200 mg bid",
"      </td>",
"      <td>",
"       Therapy with AmB should be continued until the patient shows objective evidence of improvement; treat for a total of at least 12 months; may require long-term suppression with Itr",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pregnant women",
"      </td>",
"      <td>",
"       Lipid AmB 3-5 mg/kg/day or deoxycholate AmB 0.7-1.0 mg/kg/day for severe sporotrichosis; local hyperthermia for cutaneous disease",
"      </td>",
"      <td>",
"       -",
"      </td>",
"      <td>",
"       It is preferable to wait until after delivery to treat non-life-threatening forms of sporotrichosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Children",
"      </td>",
"      <td>",
"       Itr 6-10 mg/kg/day (400 mg/day maximum) for mild disease; deoxycholate AmB 0.7 mg/kg/day for severe disease",
"      </td>",
"      <td>",
"       SSKI with increasing doses for mild disease",
"      </td>",
"      <td>",
"       Treat severe disease with an AmB formulation followed by Itr",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AmB: amphotericin B; bid: twice per day; Itr: itraconazole; SSKI: saturated solution potassium iodide.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Kauffman, CA, Bustamante, B, Chapman, SW, Pappas, PG. Clinical Practice Guidelines for the Management of Sporotrichosis: 2007 Update by the Infectious Diseases Society of America. IDSA Guidelines for Management of Sporotrichosis. Clin Infect Dis 2007; 45:1255. Copyright &copy;2007 University of Chicago Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
