var title_f31_2_31789="Influenza testing methods";
var content_f31_2_31789=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F69655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F69655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Influenza testing methods",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Time to results",
"       </td>",
"       <td class=\"subtitle1\">",
"        Acceptable specimens",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        RT-PCR (conventional gel-based PCR, real-time RT-PCR, and multiplex PCR)",
"       </td>",
"       <td>",
"        2 h",
"       </td>",
"       <td>",
"        NP swab, NP or bronchial washing, nasal or endotracheal aspirate, sputum, throat swab*",
"       </td>",
"       <td>",
"        High sensitivity and very high specificity; highly recommended; can differentiate between influenza types (A or B) and subtypes (including pandemic H1N1 influenza and avian H5N1 influenza)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\">",
"        Immunofluorescence",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        &nbsp;",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        NP swab or washing, bronchial washing, nasal or endotracheal aspirate",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        Moderately high sensitivity and high specificity; recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Direct fluorescent antibody staining",
"       </td>",
"       <td class=\"sublist_other\">",
"        2-4 h",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"       <td class=\"sublist_other\">",
"        Detects and distinguishes between influenza A and B and between A/B and other respiratory viruses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Indirect fluorescent antibody staining",
"       </td>",
"       <td class=\"sublist_other\">",
"        2-4 h",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"       <td class=\"sublist_other\">",
"        Detects and distinguishes between influenza A and B and between A/B and other respiratory viruses",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\">",
"        Rapid influenza diagnostic tests",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        &nbsp;",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        NP swab, nasal washing, nasal aspirate, throat swab*",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        Low to moderate sensitivity and high specificity; recommended; during periods of peak influenza activity, negative rapid antigen tests do not reliably exclude influenza",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Antigen detection (EIA)",
"       </td>",
"       <td class=\"sublist_other\">",
"        10-20 min",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"       <td class=\"sublist_other\">",
"        Depending on which EIA test is used, will either detect influenza A only, will detect and distinguish between influenza A and B, or will detect but not distinguish between influenza A and B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Neuraminidase detection assay",
"       </td>",
"       <td class=\"sublist_other\">",
"        20-30 min",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"       <td class=\"sublist_other\">",
"        Detects but does not distinguish between influenza A and B",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\">",
"        Viral culture",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        &nbsp;",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        NP swab, NP or bronchial washing, nasal or endotracheal aspirate, sputum, throat swab*",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        Moderately high sensitivity and highest specificity; this test is important for confirming screening test results and for public health surveillance, but it is not useful for timely clinical management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Shell viral culture",
"       </td>",
"       <td class=\"sublist_other\">",
"        48-72 h",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Isolation in cell culture",
"       </td>",
"       <td class=\"sublist_other\">",
"        3-10 days",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"       <td class=\"sublist_other\">",
"        ...",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Serologic tests (hemagglutinin inhibition, ELISA, complement-fixation, and neutralization)",
"        <sup>",
"         &loz;",
"        </sup>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Serum",
"       </td>",
"       <td>",
"        Available only in reference laboratories; not useful for timely clinical management; recommended only for retrospective diagnosis, surveillance, or research purposes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     RT-PCR: reverse-transcriptase polymerase chain reaction; NP: nasopharyngeal; EIA: enzyme immunoassay; ELISA: enzyme-linked immunosorbent assay.",
"     <br>",
"      * Throat swabs are inferior to NP specimens for the detection of influenza viruses, but may be used if NP specimens cannot be obtained.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       Requires fluorescence microscope.",
"       <br>",
"        &Delta; Includes moderately complex and Clinical Laboratory Improvement Amendments (CLIA)-waived tests.",
"        <br>",
"         <span class=\"lozenge\">",
"          &loz;",
"         </span>",
"         Requires paired acute- and convalescent-phase serum samples.",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from:",
"     <br>",
"      1. Harper SA, Bradley JS, Englund JA, et al. Seasonal Influenza in Adults and Children--Diagnosis, Treatment, Chemoprophylaxis and Institutional Outbreak Management: Clinical Practice Guidelines of the Infectious Diseases Society of America for Seasonal Influenza in Adults and Children. Clin Infect Dis 2009; 48:1003. Copyright &copy; 2009 University of Chicago Press.",
"      <br>",
"       2.&nbsp;US Centers for Disease Control and Prevention. Guidance for clinicians on the use of rapid influenza diagnostic tests. Available at:",
"       <a href=\"file://www.cdc.gov/flu/professionals/diagnosis/clinician_guidance_ridt.htm\">",
"        www.cdc.gov/flu/professionals/diagnosis/clinician_guidance_ridt.htm",
"       </a>",
"       . Accessed on June 28, 2012.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.journals.uchicago.edu/\">",
"      file://www.journals.uchicago.edu/",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
