var title_f31_22_32108="Vasculitic periph neuropathy";
var content_f31_22_32108=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F70269&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F70269&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features of vasculitic neuropathy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         Granulomatosis with polyangiitis (Wegener's) (GPA)",
"        </strong>",
"       </td>",
"       <td>",
"        Peripheral neuropathy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Polyarteritis nodosa (PAN)",
"        </strong>",
"       </td>",
"       <td>",
"        Mononeuritis multiplex",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Churg Strauss syndrome (CSS)",
"        </strong>",
"       </td>",
"       <td>",
"        Paranasal granulomas may infiltrate cranial nerves II, VI, VII via extension in WG or CSS",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <strong>",
"         Rheumatoid arthritis",
"        </strong>",
"       </td>",
"       <td>",
"        Neuropathy seen in long standing, usually rheumatoid factor positive, cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence for vasculitis includes non-healing leg ulcers and nailfold infarcts",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Sj&ouml;gren's syndrome",
"        </strong>",
"       </td>",
"       <td>",
"        May present as stocking-glove sensorimotor neuropathy, trigeminal neuropathy or ataxic large fiber neuronopathy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Systemic lupus erythematosus",
"        </strong>",
"       </td>",
"       <td>",
"        Associated with central nervous system involvement: seizures and psychiatric symptoms",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <strong>",
"         Human immunodeficiency virus",
"        </strong>",
"       </td>",
"       <td>",
"        CD4+ count &gt;200: isolated vasculitic neuropathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CD4+ count &lt;50: more extensive neuropathy and rapidly progressive; also consider CMV, toxoplasmosis, HZV, lymphoma",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        <strong>",
"         Lyme disease",
"        </strong>",
"       </td>",
"       <td>",
"        Meningitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Encephalopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Facial nerve commonly involved",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <strong>",
"         Essential mixed cryoglubulinemia",
"        </strong>",
"       </td>",
"       <td>",
"        80 percent of patients have antibodies to hepatitis C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        False positive RF",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        <strong>",
"         Temporal arteritis",
"        </strong>",
"       </td>",
"       <td>",
"        12 percent of patients have a peripheral neuropathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Jaw claudication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elevated ESR, C reactive protein",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ischemic optic neuropathy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CMV: cytomegalovirus; HZV: herpes zoster (varicella zoster) virus; ESR: erythrocyte sedimentation rate.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
