var title_f31_24_32139="NCI CTCAE for NV v4";
var content_f31_24_32139=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F69015&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F69015&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    National Cancer Institute Common Terminology Criteria for Adverse Events, nausea and vomiting",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Nausea",
"       </td>",
"       <td class=\"subtitle1\">",
"        Vomiting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1",
"       </td>",
"       <td>",
"        Loss of appetite without alteration in eating habits",
"       </td>",
"       <td>",
"        One to two episodes (separated by five minutes) in 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2",
"       </td>",
"       <td>",
"        Oral intake decreased without significant weight loss, dehydration, or malnutrition",
"       </td>",
"       <td>",
"        Three to five episodes (separated by five minutes) in 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3",
"       </td>",
"       <td>",
"        Inadequate oral caloric or fluid intake; tube feedings, TPN, or hospitalization indicated",
"       </td>",
"       <td>",
"        &ge;6 episodes (separated by five minutes) in 24 hours; tube feeding, TPN, or hospitalization indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4",
"       </td>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        Life-threatening consequences; urgent intervention indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5",
"       </td>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        Death",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     TPN: total parenteral nutrition.",
"    </div>",
"    <div class=\"reference\">",
"     With permission from: Common Terminology Criteria for Adverse Events, version 4.0, June 2010, National Institutes of Health, National Cancer Institute, file://evs.nci.nih.gov/ftp1/CTCAE/CTCAE_4.03_2010-06-14_QuickReference_5x7.pdf.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
