var title_f31_28_32203="Clinical features of syphilis";
var content_f31_28_32203=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F79946&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F79946&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical manifestation of syphilis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical manifestations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"\">",
"        Primary syphilis",
"       </td>",
"       <td>",
"        Painless ulcer (chancre), adenopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"\">",
"        Secondary syphilis",
"       </td>",
"       <td>",
"        Rash, mucocutaneous lesions, adenopathy, hepatitis, arthritis, glomerulonephritis, condyloma lata",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Latent syphilis",
"       </td>",
"       <td class=\"sublist1_start\" colspan=\"1\" rowspan=\"3\">",
"        Asymptomatic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Early latent (&lt;1 year after infection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Late latent (&gt;1 year after infection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\" rowspan=\"1\">",
"        Tertiary (late) syphilis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cutaneous disease",
"       </td>",
"       <td class=\"sublist_other\">",
"        Gummatous lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cardiovascular disease",
"       </td>",
"       <td class=\"sublist_other\">",
"        Aortic aneurysm, aortic insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Central nervous system disease (neurosyphilis)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Tabes dorsalis, Argyll-Robertson pupils, paresis, seizures, subtle psychiatric manifestations, dementia. May be asymptomatic.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
