var title_f31_28_32204="Syndromic hereditary neurop I";
var content_f31_28_32204=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F72307&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F72307&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features of hereditary peripheral neuropathies associated with other disorders-I",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"20%\">",
"     </colgroup>",
"     <colgroup width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disorder",
"       </td>",
"       <td class=\"subtitle1\">",
"        Inheritance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chromosome (gene)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Friedreich ataxia",
"       </td>",
"       <td>",
"        AR",
"       </td>",
"       <td>",
"        9q13 (frataxin)",
"       </td>",
"       <td>",
"        Usual onset before age 25 years, progressive ataxia of all limbs, optic atrophy, pyramidal signs, early loss of position and vibration sense, bladder dysfunction, distal muscle atrophy, kyphoscoliosis, cardiomyopathy, diabetes mellitus",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Infantile neuroaxonal dystrophy (PLA2G6-associated neurodegeneration)",
"       </td>",
"       <td>",
"        AR",
"       </td>",
"       <td>",
"        22q13.1 (PLA2G6)",
"       </td>",
"       <td>",
"        Usual onset at age 1 to 3 years, motor and cognitive regression, spasticity, muscle atrophy, hypotonia, cerebellar ataxia, dystonia, optic atrophy, distal sensory loss",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Tangier disease",
"       </td>",
"       <td>",
"        AC",
"       </td>",
"       <td>",
"        9q31 (ABC1)",
"       </td>",
"       <td>",
"        Orange tonsils, hepatosplenomegaly, premature coronary disease, neuropathy, absent (homozygotes) or 50 percent normal (heterozygotes) HDL cholesterol",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Abetalipoproteinemia",
"       </td>",
"       <td>",
"        A",
"       </td>",
"       <td>",
"        (MTP)",
"       </td>",
"       <td>",
"        Acanthocytes, fat malabsorption, retinitis pigmentosa, peripheral neuropathy, ataxia, sensory motor neuropathy, absent tendon reflexes, low serum cholesterol and VLDL, absent serum beta lipoprotein",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Classic Refsum disease",
"       </td>",
"       <td>",
"        A",
"       </td>",
"       <td>",
"        PHYH",
"       </td>",
"       <td>",
"        Retinitis pigmentosa, peripheral polyneuropathy, cerebellar ataxia, elevated CSF protein, sensorineural deafness, ichthyosis, anosmia, abnormal accumulation of phytanic acid",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Infantile Refsum disease",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         (PEX1)",
"        </p>",
"        <p>",
"         (PEX6)",
"        </p>",
"       </td>",
"       <td>",
"        Abnormal accumulation of phytanic acid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chediak-Higashi syndrome",
"       </td>",
"       <td>",
"        AR",
"       </td>",
"       <td>",
"        (LYST)",
"       </td>",
"       <td>",
"        Recurrent infections, partial albinism, hepatosplenomegaly, increased risk of lymphoreticular malignancy, nystagmus, seizures, mental retardation, generalized weakness, spinocerebellar degeneration, and parkinsonism",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AR: autosomal recessive; AC: autosomal codominant; A: autosomal; ABC: ATP-binding casette recorder; MTP: microsomal triglyceride transfer protein; PHYH: phytanoyl-CoA hydroxylase.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
