var title_f31_29_32220="Tx acute HAE";
var content_f31_29_32220=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F87450&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F87450&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of acute episodes of hereditary angioedema in adults and children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Medication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Availability",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dosing",
"       </td>",
"       <td class=\"subtitle1\">",
"        Precautions",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         C1 inhibitor concentrate (plasma-derived)",
"        </p>",
"        (Berinert, Berinert P) (Cinryze)",
"       </td>",
"       <td>",
"        In US:",
"        <ul>",
"         <li>",
"          Berinert",
"         </li>",
"         <li>",
"          Cinryze",
"         </li>",
"        </ul>",
"        Other countries:",
"        <ul>",
"         <li>",
"          Berinert P",
"         </li>",
"         <li>",
"          Cinryze",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        20 units per kg body weight given IV over 10 minutes. Symptoms usually stabilize in 30 minutes. Second dose uncommonly needed but may be given 30 minutes to&nbsp;two hours after first.",
"       </td>",
"       <td>",
"        Do not shake solution because protein will denature. Cinryze not FDA-approved for acute attacks, but has efficacy.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Recombinant C1 inhibitor",
"        </p>",
"        Conestat alfa (Ruconest) (Rhucin)",
"       </td>",
"       <td>",
"        Europe only",
"       </td>",
"       <td>",
"        50 units per kg body weight. Second dose rarely needed.",
"       </td>",
"       <td>",
"        Patients should be screened for rabbit allergy prior to receiving, with rabbit-specific IgE immunoassay and not receive drug if positive.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Bradykinin B2 receptor antagonist",
"        </p>",
"        Icatibant (Firazyr)",
"       </td>",
"       <td>",
"        US and many other countries",
"       </td>",
"       <td>",
"        30 mg slow subcutaneous infusion (because of volume) in abdominal area. Second dose needed in about 10 percent of patients and can be given six hours after first. Max of three doses in 25 hours.",
"       </td>",
"       <td>",
"        Caution in patients with unstable angina. Mild injection site reactions are common.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Kallikrein inhibitor",
"        </p>",
"        Ecallantide (Kalbitor)",
"       </td>",
"       <td>",
"        US only",
"       </td>",
"       <td>",
"        30 mg (3 doses of 10 mg each) given at three separate sites, subcutaneously in abdomen, upper arm, or thigh and away from site of angioedema.",
"       </td>",
"       <td>",
"        Rare allergy reaction reported usually in &lt;1 hour. Should be administered by a clinician or nurse in a medical facility equipped to treat anaphylaxis.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plasma",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Solvent detergent treated plasma (S/D plasma) (preferred)",
"         </li>",
"         <li>",
"          Fresh frozen plasma (FFP)&nbsp;",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        2 units initially. Can be repeated every two to four hours if needed.",
"       </td>",
"       <td>",
"        Monitor for volume overload in patients with underlying conditions predisposing to volume overload. Theoretical risk of transmission of blood borne pathogens.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
