" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Male reproductive physiology",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?31/3/31800/contributors\">",
