" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathophysiology of familial Mediterranean fever",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/32/32261/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
