" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Urinary diversion and reconstruction following cystectomy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
