var title_f31_34_32299="DSM-IV-TR diagnostic criteria for cannabis dependence";
var content_f31_34_32299=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F64590&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F64590&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    DSM-IV-TR diagnostic criteria for cannabis dependence",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       A maladaptive pattern of cannabis use resulting in clinically significant impairment or distress, as indicated by three or more of the following at any time during the same 12 month period:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       1. Tolerance, defined by either of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       a. Using markedly increased amounts of cannabis to achieve the desired effect or intoxication.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       b. Markedly diminished effect with continued use of the same amount of cannabis.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Cannabis is often taken in larger amounts or over a longer period than was intended.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. There is a persistent desire or there are unsuccessful efforts to cut down or control cannabis use.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. A great deal of time is spent obtaining cannabis, using it, or recovering from its effects.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Important social, occupational, or recreational activities are neglected because of cannabis use.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Persistent cannabis use despite knowledge of having a recurrent or ongoing physical or psychological problem that is probably caused or exacerbated by cannabis, such as a chronic cough related to smoking or a decrease in goal related activities.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted with permission from: the Diagnostic and Statistical Manual of Mental Disorders, Text Revision, Fourth Edition (Copyright 2000). American Psychiatric Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
