var title_f31_34_32300="Guidelines for family and couple therapy";
var content_f31_34_32300=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F78200&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F78200&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    A practical guide for family and couples therapy for depression",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Principles and tasks",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meet with the family",
"       </td>",
"       <td>",
"        Include as many family members as can come and are interested in helping.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elicit everyone's view",
"       </td>",
"       <td>",
"        All family members should discuss their perception of the family's problems, whether related to the depression or not.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assess couples/family functioning",
"       </td>",
"       <td>",
"        Evaluate how the family communicates, solves problems, connects emotionally with each other, assigns family responsibilities, and sets family rules.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Focus on strengths",
"       </td>",
"       <td>",
"        Identify and reinforce family strengths.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluate motivation for change",
"       </td>",
"       <td>",
"        Ensure that families are committed to working together on their problems.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assess safety",
"       </td>",
"       <td>",
"        Ensure that the depressed patient is not suicidal and that there is no family violence.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Psychoeducation",
"       </td>",
"       <td>",
"        Teach families about depression, including signs and symptoms, etiology, course of illness, and available treatments.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prioritize problems",
"       </td>",
"       <td>",
"        Determine with the family which of their problems they want to address. Prioritize problems related to safety.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Negotiate expectations",
"       </td>",
"       <td>",
"        Establish clinician's expectations for the treatment process, eg, compliance with meetings and working on assigned tasks. Family members should negotiate expected changes with each other.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Set realistic expectations",
"       </td>",
"       <td>",
"        Expectations about change and outcome should be realistic and achievable.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Focus on behaviors and not feelings",
"       </td>",
"       <td>",
"        Feelings cannot be changed and are difficult to measure. Behaviors are more amenable to change and can be evaluated more objectively.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Include some general goals",
"       </td>",
"       <td>",
"        Communicate directly and clearly, support and fulfill appropriate roles as spouse and parent, and reduce criticism, blame, and hostility.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Support increased level of activity",
"       </td>",
"       <td>",
"        The depressed patient should gradually increase participation in activities first within the home and then outside of the home, including greater levels of physical activity.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide feedback",
"       </td>",
"       <td>",
"        Inform the family when they are doing well and point out difficulties they are experiencing in making changes.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
