var title_f31_39_32374="Direct laryngoscopy straight blade";
var content_f31_39_32374=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F77129&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F77129&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 474px\">",
"   <div class=\"ttl\">",
"    Direct laryngoscopy with a straight blade",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 454px; height: 608px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    Direct laryngoscopy using a straight blade.",
"    <br>",
"     (A) The head is positioned with the right hand, and the mouth is opened using the fifth finger of the left hand (if necessary).",
"     <br>",
"      (B) Alternately, the mouth may be opened with the thumb and index finger of the right hand using a \"scissor\" technique.",
"      <br>",
"       (C) The laryngoscope blade is inserted under direct vision over the tongue and into the vallecula. One attempt may be made at this point to elevate the epiglottis by lifting upward on the laryngoscope handle at a 45-degree angle.",
"       <br>",
"        (D) If unsuccessful, the tip of the blade is used to directly retract the epiglottis, revealing the vocal cords and glottic opening.",
"        <div class=\"footnotes\">",
"        </div>",
"        <div class=\"reference\">",
"         Reproduced with permission from: King C, Rappaport LD. Emergent endotracheal intubation. In: Textbook of Pediatric Emergency Procedures, 2nd ed, King C, Henretig FM (Eds), Lippincott Williams &amp; Wilkins, Philadelphia 2008. Copyright &copy; 2008 Lippincott Williams &amp; Wilkins.",
"        </div>",
"        <div class=\"contractual\">",
"         <br/>",
"         <a href=\"file://www.lww.com\">",
"          file://www.lww.com",
"         </a>",
"        </div>",
"       </br>",
"      </br>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
