" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Evaluation of pulmonary disability",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/40/32391/contributors\">",
