var title_f31_41_32411="Clinical group rhabdomyosarcoma";
var content_f31_41_32411=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F59875&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F59875&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical grouping of rhabdomyosarcoma by the intergroup rhabdomyosarcoma study group (IRSG)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Extent of disease/surgical result",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        I",
"       </td>",
"       <td>",
"        A Localized tumor, confined to site of origin, completely resected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B Localized tumor, infiltrating beyond site of origin, completely resected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        II",
"       </td>",
"       <td>",
"        A Localized tumor, gross total resection, but with microscopic residual disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B Locally extensive tumor (spread to regional lymph nodes), completely resected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C Locally extensive tumor (spread to regional lymph nodes), gross total resection, but microscopic residual disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        III",
"       </td>",
"       <td>",
"        A Localized or locally extensive tumor, gross residual disease after biopsy only",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B Localized or locally extensive tumor, gross residual disease after major resection (&ge;50 percent&nbsp;debulking)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV",
"       </td>",
"       <td>",
"        Any size primary tumor, with or without regional lymph node involvement, with distant metastases, irrespective of surgical approach to primary tumor",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Modified from Crist W, Gehan EA, Ragab AH, et al. The Third Intergroup Rhabdomyosarcoma Study. J Clin Oncol 1995; 13:610; and Crist W, Garnsey L, Beltangady M, et al. Prognosis in children with rhabdomyosarcoma: A report of the intergroup rhabdomyosarcoma studies I and II. Intergroup Rhabdomyosarcoma Committee. J Clin Oncol 1990; 8:443.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
