var title_f31_41_32412="Ingredient label terms for peanut";
var content_f31_41_32412=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F75071&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F75071&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    How to read an ingredient label for a peanut-free diet",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Avoid foods that contain peanuts or any of these ingredients:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Artificial nuts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Beer nuts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cold pressed, expeller pressed, or extruded peanut oil",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Goobers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ground nuts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mixed nuts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Monkey nuts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut meat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut pieces",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peanut butter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peanut flour",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peanut protein hydrolysate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Peanut is sometimes found in the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        African, Asian (especially Chinese, Indian, Indonesian, Thai, and Vietnamese), and Mexican dishes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Baked goods (eg, pastries, cookies)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Candy (including chocolate candy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chili",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Egg rolls",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enchilada sauce",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Marzipan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mole sauce",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nougat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Keep the following in mind:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Mandelonas are peanuts soaked in almond flavoring.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;",
"        <strong>",
"         The FDA exempts highly refined peanut oil from being labeled as an allergen.",
"        </strong>",
"        Studies show that most allergic individuals can safely eat peanut oil that has been highly refined (not cold pressed, expeller pressed, or extruded peanut oil). Follow your doctor's advice.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; A study showed that unlike other legumes, there is a strong possibility of cross-reaction between peanuts and lupine.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Arachis oil is peanut oil.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Many experts advise patients allergic to peanuts to avoid tree nuts as well.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Sunflower seeds are often produced on equipment shared with peanuts.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    All FDA-regulated manufactured food products that contain peanut as an ingredient are required by US law to list the word \"peanut\" on the product label.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Copyright &copy; 2010 The Food Allergy &amp; Anaphylaxis Network. Used with permission.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
