" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Preoperative assessment of hemostasis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
