var title_f31_43_32444="UNOS priority status for HT";
var content_f31_43_32444=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F83792&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F83792&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    UNOS priority status for heart transplant",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Status 1A",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <ul>",
"         <li>",
"          Reside in the transplant listing center and at least one of the following:",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <p>",
"         &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&nbsp; On mechanical ventilation",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp; On intraaortic balloon pump, total artificial heart, or&nbsp;extracorporeal&nbsp;membrane oxygentator",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&nbsp; Hemodynamic monitoring with single high-dose intravenous inotrope or multiple intravenous inotropes (eg, dobutamine &gt;7.5 mcg/kg/min plus milrinone 0.5 mcg/kg/min)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <ul>",
"         <li>",
"          &nbsp;For 30 days after implantation with a left and/or right ventricular assist device for acute decompensation (need not be admitted to the listing center)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <ul>",
"         <li>",
"          &nbsp; A candidate with a total artificial heart who has been discharged from the listing center may be listed as Status 1A for 30 days after the discharge. (This provision will expire on December 1, 2012.)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <ul>",
"         <li>",
"          LVAD with a device-related complication (such as thromboembolism, device infection, mechanical failure, or life-threatening ventricular arrhythmias)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Status 1B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <ul>",
"         <li>",
"          Infusion of intravenous inotropes or&nbsp;supported by an implanted chronic (left&nbsp;and/or right)&nbsp;mechanical assist device",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <ul>",
"         <li>",
"          &nbsp;A candidate with a total artificial heart who has been discharged from the listing hospital may be listed as Status 1B at ay point in time after discharge.",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Status 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        All candidates who do not meet 1A or 1B requirements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Status 7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <p>",
"         Temporarily unsuitable to receive a thoracic organ transplant.&nbsp;During the inactive period there is no gain or loss of&nbsp;accrued time.",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Based on guidelines from: Organ Procurement and Transplantation Network (OPTN). Organ Distribution: Allocation of Thoracic Organs. Department of Health and Human Services, Health Resources and Services Administration, Healthcare Systems Bureau, Division of Transplantation, November 15, 2011. file://optn.transplant.hrsa.gov/policiesAndBylaws/policies.asp (Accessed on May 31, 2012).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
