var script_f31_44_32448=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function VSDSReflux_fx() {",
"with(document.VSDSReflux_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1.checked){",
"Score = Score + 0.5;",
"}",
"if (cc2.checked){",
"Score = Score + 1;",
"}",
"if (cc3.checked){",
"Score = Score + 0.5;",
"}",
"if (cc4.checked){",
"Score = Score + 2;",
"}",
"if (cc5.checked){",
"Score = Score + 1;",
"}",
"if (cc6.checked){",
"Score = Score + 1;",
"}",
"if (cc7.checked){",
"Score = Score + 1;",
"}",
"if (cc8[0].checked){",
"Score = Score + 1;",
"}",
"if (cc8[1].checked){",
"Score = Score + 2;",
"}",
"if (cc9.checked){",
"Score = Score + 1;",
"}",
"cctotal.value = Score;",
"}",
"}",
"",
"",
"",
"",
"",
"",
""].join("\n");
