"  <div id=\"topicContent\">",
"   <div id=\"topicTitle\">",
"    Epidemiology and clinical features of multiple sclerosis in adults",
"   </div>",
"   <div id=\"topicContributors\">",
"    <div>",
"     <a id=\"authors\">",
"     </a>",
"     <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/44/32458/contributors\">",
"      Author",
"     </a>",
"     <br/>",
"     <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?31/44/32458/contributors\">",
"      Michael J Olek, DO",
"     </a>",
"     <br/>",
"    </div>",
"    <div>",
"     <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/44/32458/contributors\">",
"      Section Editor",
"     </a>",
"     <br/>",
"     <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?31/44/32458/contributors\">",
"      Francisco Gonzalez-Scarano, MD",
"     </a>",
"     <br/>",
"    </div>",
"    <div>",
"     <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/44/32458/contributors\">",
"      Deputy Editor",
"     </a>",
"     <br/>",
"     <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?31/44/32458/contributors\">",
"      John F Dashe, MD, PhD",
"     </a>",
"     <br/>",
"    </div>",
"   </div>",
"   <div id=\"disclosures\">",
"    <a href=\"mobipreview.htm?31/44/32458/contributor-disclosure\" target=\"_blank\">",
"     Disclosures",
"    </a>",
"   </div>",
