var title_f31_45_32476="Causes of hyperthyroidism";
var content_f31_45_32476=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F76972&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F76972&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of hyperthyroidism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Hyperthyroidism with a normal or high radioiodine uptake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Autoimmune thyroid disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Graves' disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hashitoxicosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Autonomous thyroid tissue (uptake may be low if recent iodine load led to iodine-induced hyperthyroidism)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Toxic adenoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Toxic multinodular goiter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        TSH-mediated hyperthyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        TSH-producing pituitary adenoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Non-neoplastic TSH-mediated hyperthyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Human chorionic gonadotropin-mediated hyperthyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hyperemesis gravidarum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Trophoblastic disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Hyperthyroidism with a near absent radioiodine uptake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Thyroiditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Subacute granulomatous (de Quervain's) thyroiditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Painless thyroiditis (silent thyroiditis, lymphocytic thyroiditis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Postpartum thyroiditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Amiodarone (also may cause iodine-induced hyperthyroidism)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Radiation thyroiditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Palpation thyroiditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Exogenous thyroid hormone intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Excessive replacement therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intentional suppressive therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Factitious hyperthyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Ectopic hyperthyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Struma ovarii",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Metastatic follicular thyroid cancer",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Major causes of hyperthyroidism according to the presence of a high or low radioiodine uptake. High uptake indicates increased new hormone synthesis by the thyroid whereas low uptake indicates release of preformed hormone, exogenous ingestion, or extrathyroidal hormone synthesis.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
