var title_f31_50_32555="Difficult patient characteristics";
var content_f31_50_32555=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F66724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F66724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Patient characteristics that lead physicians to label them difficult",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patients' expressions of emotion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patients' personality/characterologic features:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obsessive-compulsive; dependent; self-defeating; histrionic; narcissistic; paranoid; and schizoid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patients' personal characteristics:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Language and other communication barriers (deaf, blind); malodorous; communicable disease; racial/ethnic; impecunious; higher/lower social status; different sexual orientation from the doctor; and many other features that make the patient different from the doctor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patients' specific clinical problems:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Somatization (including chronic pain); drug/alcohol abuse; psychiatric problems; sexual problems; organic mental syndromes; cancer; suicidal; difficult medical problem and getting worse; undiagnosed medical problem in spite of evaluation; noncompliance; giving bad news; death/dying; advance directives; and adverse health habits",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patients' direct demands/stresses on the physician:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Threat of dismissal; patient disagrees with an explanation; patient complains (about care, the HMO); patient requires more time than available; seductive patient; aggressive or threatening patient; many legal/insurance papers to be filled out; unnecessary lab/referral/medication requests; unnecessary disability request; patient takes control of interaction; and patient wants social interaction or advice about nonmedical issues.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
