var title_f31_53_32604="Properties of dressings";
var content_f31_53_32604=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=SURG%2F60931&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=SURG%2F60931&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Properties of dressings",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category (product examples)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Property",
"       </td>",
"       <td class=\"subtitle1\">",
"        Indication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Simple, low-adherance",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Cotton or Viscose backing and a non-adherent coating which may",
"be polyethylene, polypropylene or artificial silk. Generally are",
"nonabsorbent.",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Superficial dry wound, or wound with light exudate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        (Telfa, Cutinova Nonadherent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Paraffin gauze",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Gauze fiber coated with petrolatum. Generally are nonabsorbent.",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Superficial dry wound, or wound with exudate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        (Vaseline Adaptic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Semipermeable adhesive films",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Thin layer of polyurethane alone",
"(Omiderm) or with an adhesive (Opsite). They permit the passage of",
"gases and water vapor but not exudate, have a slight drying effect of",
"the wound, and are transparent and allow visualization of the wound.",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Wound with light exudate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        (Omiderm, Opsite, Tegaderm, Bioocclusive)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Hydrogels",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Made of starch, giving them a large capacity for absorbing water without changing the shape of the dressing.",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        <p>",
"         Flat wound with moderate exudate",
"        </p>",
"        <p>",
"         Useful over crusted and dried surface exudate",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        (Vigilon, Spenco Second Skin)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Hydrocolloid",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Generally",
"composed of gelatin and pectin, which dissolve in the presence of an",
"exudate, absorbing it and becoming space occupying.",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Wounds with moderate exudate, and deep or cavity wounds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        (DuoDerm, Granuflex, Tegasorb, Biofilm)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Alginate",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Calcium",
"ions in the alginate fiber exchange with sodium ions of the exudate to",
"form a fibrous gel. Calcium ions in Kaltostat activate the clotting",
"cascade.",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Wounds with heavy exudate, and deep or cavity wounds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        (Sorbsan, Kaltostat, Cutinova Cavity)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Ryan, TJ, Derm Clin 1993; 11:207, and Local applications for wounds - II, Drug Ther Bull 1991; 29:97.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
