var title_f31_54_32619="Antifibrotic Rx for scleroderma";
var content_f31_54_32619=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F81334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F81334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antifibrotic therapies for systemic sclerosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Treatment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D-penicillamine",
"       </td>",
"       <td>",
"        Inhibits the formation of stable collagen cross-links by forming hydroxy-lysine aldehyde and lysine groups on collagen precursors.",
"       </td>",
"       <td>",
"        Widely used in UK, doses above 750 mg daily probably needed for any benefit. Open studies have shown beneficial effects on lung, skin and kidney disease in SSc. Placebo controlled trial currently underway in USA.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interferon alpha",
"       </td>",
"       <td>",
"        Inhibits collagen production by dermal SSc fibroblasts in vitro, at transcriptional level. May also eliminate high collagen producing fibroblast subpopulations.",
"       </td>",
"       <td>",
"        Efficacy has been confirmed in open studies and a placebo controlled trial is currently underway in UK. Benefits must be balanced by the considerable morbidity of IFN administration.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interferon gamma",
"       </td>",
"       <td>",
"        Similar to interferon alpha.",
"       </td>",
"       <td>",
"        Initial support for use from open studies and in vitro properties, but subsequent evaluation has been less favorable.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colchicine",
"       </td>",
"       <td>",
"        Inhibition of collagen production by disrupting microtubule formation in fibroblast cytoskeleton. Possible enhancement of collagenase activity.",
"       </td>",
"       <td>",
"        Little used in most centers.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
