var title_f31_55_32637="Echo in mitral stenosis";
var content_f31_55_32637=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F68079%7ECARD%2F52663%7ECARD%2F68069&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=25\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F68079%7ECARD%2F52663%7ECARD%2F68069&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=25\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Two-dimensional and Doppler echocardiography in mitral stenosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Class I - There is evidence and/or general agreement that echocardiography should be performed for the following reasons in patients with mitral stenosis:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Diagnosis of mitral stenosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Assessment of severity (mean gradient, mitral valve area, pulmonary artery pressure)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Assessment of concomitant valvular lesions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Assessment of mitral valve morphology to determine suitability for percutaneous mitral balloon valvotomy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Known mitral stenosis with changing signs and symptoms",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Assessment of the hemodynamic response of the mean gradient and pulmonary artery pressure by exercise Doppler echocardiography when there is a discrepancy between the the resting Doppler echocardiographic findings and clinical findings, symptoms, and signs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Class IIa - The weight of evidence or opinion is in favor of usefulness of echocardiography in patients with mitral stenosis in the following setting:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Reevaluation of asymptomatic patients who are clinically stable to assess pulmonary artery pressure every year with severe mitral stenosis, every one to two years with moderate mitral stenosis, and every three to five with mild mitral stenosis",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Transesophageal echocardiography in mitral stenosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general",
"agreement that transesophageal echocardiography should be performed for",
"the following reasons in patients with mitral stenosis:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; To assess the presence of absence of left atrial thrombus and to further evaluate the severity of mitral regurgitation in patients being considered for percutaneous mitral balloon valvotomy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; To assess mitral valve morphology and hemodynamics if transthoracic echocardiography provides suboptimal data",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III - There is evidence and/or",
"general agreement that transesophageal echocardiography is not useful",
"in patients with mitral stenosis in the following setting:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Transthoracic echocardiography provides satisfactory data on mitral valve morphology and hemodynamics",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Cardiac catheterization in mitral stenosis (MS)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general",
"agreement that cardiac catheterization for hemodynamic evaluation is",
"useful in patients with MS in the following settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; To assess the severity of MS if noninvasive tests are not conclusive or there is a discrepancy between the results of noninvasive tests and clinical findings related to the severity of MS.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; When there is a discrepancy between mitral valve area and the Doppler-derived mean gradient; catheterization should include left ventriculography to evaluate the severity of mitral regurgitation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa - The weight of evidence or",
"opinion is in favor of usefulness of cardiac catheterization for",
"hemodynamic evaluation in patients with MS in the following settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; To assess the exercise-induced hemodynamic response of the pulmonary artery and left atrial pressures when there is a discrepancy between clinical symptoms and hemodynamics at rest.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; To assess the cause of severe pulmonary arterial hypertension if it is out of proportion to the severity of MS determined by noninvasive testing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III - There is evidence and/or",
"general agreement that cardiac catheterization for hemodynamic",
"evaluation in patients with MS is not useful in in the following setting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; To assess mitral valve hemodynamics when two-dimensional and Doppler echocardiographic findings are consistent with clinical findings.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f31_55_32637=[""].join("\n");
