var title_f31_6_31851="Frequency of laboratory tests following kidney transplantation";
var content_f31_6_31851=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F80400&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F80400&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested frequency of laboratory tests following kidney transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Basic chemistry panel (including eGFR)",
"       </td>",
"       <td>",
"        Every visit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Complete blood count",
"       </td>",
"       <td>",
"        Every visit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tacrolimus/cyclosporine/sirolimus level",
"       </td>",
"       <td>",
"        Every visit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinalysis/protein to creatinine ratio",
"       </td>",
"       <td>",
"        Every visit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HbA1C",
"       </td>",
"       <td>",
"        Every three months/or visit if less frequent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lipid profile",
"       </td>",
"       <td>",
"        Every three months/or visit if less frequent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin D and PTH",
"       </td>",
"       <td>",
"        Immediately post-transplant and then every 6 to 12 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BK blood and/or BK urine",
"       </td>",
"       <td>",
"        Monthly for the first 6 months, and then at 9, 12, 18, and 24 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bone density scan (optional)",
"       </td>",
"       <td>",
"        At three months and then every one to two years",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Anil Chandraker, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
