var title_f31_6_31852="Smoking consequences among adolescents with chronic illnesses";
var content_f31_6_31852=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F61284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F61284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Consequences of smoking among adolescents with chronic illnesses",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Chronic illness",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential disease-specific health consequences",
"       </td>",
"       <td class=\"subtitle1\">",
"        References",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asthma",
"       </td>",
"       <td>",
"        Increased frequency and severity of exacerbations, medication use, hospitalization, risk of respiratory arrest.",
"       </td>",
"       <td>",
"        <p>",
"         Larsson L. Thorax. 1994; 50:260.",
"        </p>",
"        <p>",
"         LeSon S. J Asthma. 1995; 32:379.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Cystic fibrosis",
"       </td>",
"       <td>",
"        Increased frequency and severity of bacterial lung infection and hospitalization; accelerated decline in lung function, worse nutritional status.",
"       </td>",
"       <td>",
"        <p>",
"         Britto MT. Pediatrics. 1998; 101:250.",
"        </p>",
"        <p>",
"         Verma A. J R Soc Med. 2001; 94 (suppl 40):29.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Juvenile idiopathic (rheumatoid) arthritis",
"       </td>",
"       <td>",
"        Increased disease severity; increased risks for cardiovascular disease and premature death. Exacerbation of osteopenia.",
"       </td>",
"       <td>",
"        <p>",
"         Nash AA. Arthritis Care Res. 1998; 11:391.",
"        </p>",
"        <p>",
"         Gordon MM. Ann Rheum Dis. 2003; 61:66.",
"        </p>",
"        <p>",
"         French AR. J Rheumatol. 2002; 29:1065.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Cancer",
"       </td>",
"       <td>",
"        Increased risks from several cancer treatments; respiratory infections when immunosuppressed; worse mucositis.",
"       </td>",
"       <td>",
"        <p>",
"         O'Driscoll BR. N Engl J Med. 1990; 323:378.",
"        </p>",
"        <p>",
"         Lipshultz SE. N Engl J Med. 1991; 324.",
"        </p>",
"        <p>",
"         Tyc VL. J Child Health Care. 2004; 8:288.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Sickle cell disease",
"       </td>",
"       <td>",
"        Increased risk of acute chest syndrome; possible increased risk of stroke.",
"       </td>",
"       <td>",
"        <p>",
"         Young RC Jr. J Natl Med Assoc. 1992; 84:267.",
"        </p>",
"        <p>",
"         Britto MT. Pediatrics. 1998; 101:250.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Diabetes mellitus",
"       </td>",
"       <td>",
"        Accelerated cardiovascular and peripheral vascular disease, including atherosclerosis, retinopathy, and nephropathy. Smoking increases the morbidity and mortality of type 1 diabetes by 50-75 percent.",
"       </td>",
"       <td>",
"        <p>",
"         Frey MA. J Adolesc Health. 1997; 20:38.",
"        </p>",
"        <p>",
"         Cundiff DK. MedGenMed. 2002; 4:2.",
"        </p>",
"        <p>",
"         Gay EC. Diabetes Care. 1992; 15:947.",
"        </p>",
"        <p>",
"         Karim R. Diabet Med 2005; 22:81.",
"        </p>",
"        <p>",
"         Hsu CC. Diabet Med 2010; 27:295.",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Tyc VL, Throckmorton-Belzer L. Pediatrics 2006; 118:e471, with additional sources as indicated in the table.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
