var script_f31_61_32732=[""].join("\n");
var outline_f31_61_32732=null;
var title_f31_61_32733="Physical findings in the hypertensive child";
var content_f31_61_32733=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F76824&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F76824&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Physical examination findings associated with possible etiology of hypertension in children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Physical examination finding",
"       </td>",
"       <td class=\"subtitle1\">",
"        Possible etiology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        General",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Height and weight",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Obesity",
"       </td>",
"       <td>",
"        Essential hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Truncal obesity",
"       </td>",
"       <td>",
"        Consider Cushing's syndrome, Steroid Rx",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Growth retardation",
"       </td>",
"       <td>",
"        Consider chronic renal disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Vital signs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tachycardia",
"       </td>",
"       <td>",
"        Catecholamine excess or hyperthyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        BP&nbsp;differences in&nbsp;extremities",
"       </td>",
"       <td>",
"        If upper extremity BP &gt; lower extremity BP, consider coarctation of aorta",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Head and neck",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Elfin facies",
"       </td>",
"       <td>",
"        Williams syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Moon facies",
"       </td>",
"       <td>",
"        Cushing syndrome,&nbsp;corticosteroid therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Thyroid enlargement",
"       </td>",
"       <td>",
"        Hyperthyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Webbed neck",
"       </td>",
"       <td>",
"        Turner syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tonsillar hypertrophy",
"       </td>",
"       <td>",
"        Sleep-disordered breathing, sleep apnea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Eye",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Retinal changes",
"       </td>",
"       <td>",
"        Suggest severe HTN and secondary etiology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Papilledema",
"       </td>",
"       <td>",
"        Intracranial HTN",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acne, hirsutism, striae",
"       </td>",
"       <td>",
"        Cushing syndrome, Steroid Rx",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Caf&eacute;-au-lait spots and/or neurofibromas",
"       </td>",
"       <td>",
"        Neurofibromatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ash leaf spots and/or adenoma sebaceum",
"       </td>",
"       <td>",
"        Tuberous sclerosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Rash",
"       </td>",
"       <td>",
"        Secondary renal disease: lupus,",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Henoch-Sch&ouml;nlein purpura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acanthrosis nigricans",
"       </td>",
"       <td>",
"        Type 2 diabetes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Chest",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Murmur",
"       </td>",
"       <td>",
"        Coarctation of the aorta",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Apical heave",
"       </td>",
"       <td>",
"        L ventricular hypertrophy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Abdomen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Abdominal bruit",
"       </td>",
"       <td>",
"        Reno-vascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mass",
"       </td>",
"       <td>",
"        Hydronephrosis, polycystic kidney disease, renal tumors, neuroblastoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Extremities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulse",
"       </td>",
"       <td>",
"        LE pulse &lt; UE suggest coarctation of aorta",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Traction/casts",
"       </td>",
"       <td>",
"        Orthopedic manipulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asymmetry of limbs",
"       </td>",
"       <td>",
"        Beckwith-Wiedeman syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Arthritis",
"       </td>",
"       <td>",
"        Henoch-Sch&ouml;nlein purpura, collagen vascular disease (lupus)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Neurologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Muscle weakness",
"       </td>",
"       <td>",
"        Liddle syndrome, Hyperaldosteronism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ascending paralysis",
"       </td>",
"       <td>",
"        Guillain-Barre syndrome, polio",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diminished pain response",
"       </td>",
"       <td>",
"        Familial dysautonomia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Genitalia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ambiguous/virilization",
"       </td>",
"       <td>",
"        Adrenal hyperplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Advanced puberty",
"       </td>",
"       <td>",
"        Intracranial tumors",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from the Fourth report on the diagnosis, evaluation, and treatment of high blood pressure in children and adolescents. National Heart, Lung and Blood Institute. National Institutes of Health. May 2004.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
